function parseDate($date)
{

    $date = DateTime::createFromFormat('Y-m-d\TH:i:s+', $date);
    $date_utc = $date->format('H:i - d.m.Y') . ' (UTC)';

    $date_unix = mktime($date->format("H"), $date->format("i"), 0, $date->format("m"), $date->format("d"), $date->format("Y"));
    $now = time();

    $date_unix += 60 * 120; // add 2h for timezone difference
    $diff = $now - $date_unix;
    $minago = (int)($diff / 60);

    return 'Last Update: <i>' . $minago . ' min ago</i>
<i>' . $date_utc . '</i>';

}


function searchForId($id, $array)
{
    $result = array_search($id, $array);
    return $result === false ? null : $result;
}


// Method: POST, PUT, GET etc
// Data: array("param" => "value") ==> index.php?param=value

function callCovidApi($method, $url, $data = false)
{
    $curl = curl_init();

    switch ($method) {
        case "POST":
            curl_setopt($curl, CURLOPT_POST, 1);

            if ($data)
                curl_setopt($curl, CURLOPT_POSTFIELDS, $data);
            break;
        case "PUT":
            curl_setopt($curl, CURLOPT_PUT, 1);
            break;
        default:
            if ($data)
                $url = sprintf("%s?%s", $url, http_build_query($data));
    }

    // Optional Authentication:
    #curl_setopt($curl, CURLOPT_HTTPAUTH, CURLAUTH_BASIC);
    #curl_setopt($curl, CURLOPT_USERPWD, "username:password");

    curl_setopt($curl, CURLOPT_URL, $url);
    curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);

    $result = curl_exec($curl);

    curl_close($curl);

    return $result;
}
